void mainImage(out vec4 O, in vec2 I) {
    vec2 uv = I / iResolution.xy;
    O = texture(iChannel0, uv);
}

#define R iResolution

const float PI = 3.14159265359;

float getEdge(float dist) {
    float edge = smoothstep(.3, .2, dist) * pow(dist / .3, 20.);
	return edge;
}

void mainImage(out vec4 O, in vec2 I) {
    vec2 uv = I / R.xy;
    vec2 cartUv = (I - (R.xy / 2.)) / R.y;
  	float angle = atan(cartUv.x, cartUv.y);
    float dist = length(cartUv) * 2.;
    float fft = texture(iChannel1, vec2(1. - (abs(angle) / PI), .33)).r; 
    vec3 color = vec3(
        getEdge(dist),
        getEdge(dist - 0.01),
        getEdge(dist - 0.02)
    );
    vec3 prevColor = texture(iChannel0, uv).rgb;    
    prevColor = texture(
        iChannel0,
        uv
        	- vec2(cartUv * fft * .03)
        	- vec2(prevColor.r - .3, prevColor.b - .3) * 0.003 * dist
    ).rgb;    
    prevColor *= .997;
    color += prevColor;
    color = clamp(color, 0., 1.);
    O = vec4(color, 1.);
}
