const float PI = 3.1415926535897932384626433832795;
// some primitive functions (returning positive number inside and negative number outside)
float box(float x,float y) {
	return 1.0 - max(abs(x),abs(y));
}

float triangle(float x,float y) {
	return min(0.0 - (x + y),min( 1.0 + x, 1.0 + y));
}

// some linear transformations of the arguments (incapsulated in vector v) of implicit functions

vec2 rotate(float a,vec2 v) {
	float c_a = cos(a);
	float s_a = sin(a);
	float x1 = v.x * c_a + v.y * s_a;
	float y1 = v.x * -s_a + v.y * c_a;
	v.x = x1;
	v.y = y1;
    return v;
}
vec2 scale(float s1,float s2,vec2 v) {
	v.x /= s1;
	v.y /= s2;
    return v;
}

vec2 translate(vec2 dv,vec2 v) {
	v.x -= dv.x;
	v.y -= dv.y;
    return v;
}

float axes(float x, float y)
{
    float axis_y;
    float axis_x;
    if(abs(x)< 0.1) axis_y=1.0;
    if(abs(y)< 0.1) axis_x=1.0;
    return   axis_y+axis_x;           
}

float grid(float x, float y)
{
    float grid_y = 0.0;
    float grid_x = 0.0;
    if(abs(mod(x,1.0)) < 0.15) grid_x=1.0;
    if(abs(mod(y,1.0)) < 0.15) grid_y=1.0;
    return (grid_y+grid_x)/3.0;
}

float drawA(float x, float y)
{
    vec2 leftLegAV = vec2(x, y);
	leftLegAV = translate(vec2(5, 11), leftLegAV);
	leftLegAV = scale(2.0, 8.0, leftLegAV);
	float leftLegA = box(leftLegAV[0], leftLegAV[1]);

	vec2 rightLegAV = vec2(x, y);
	rightLegAV = translate(vec2(21, 11), rightLegAV);
	rightLegAV = scale(2.0, 8.0, rightLegAV);
	float rightLegA = box(rightLegAV[0], rightLegAV[1]);

	vec2 midLineAV = vec2(x, y);
	midLineAV = translate(vec2(13, 13), midLineAV);
	midLineAV = rotate(PI / 2.0, midLineAV);
	midLineAV = scale(2.0, 6.0, midLineAV);
	float midLineA = box(midLineAV[0], midLineAV[1]);

	vec2 topLineAV = vec2(x, y);
	topLineAV = translate(vec2(13, 21), topLineAV);
	topLineAV = rotate(PI / 2.0, topLineAV);
	topLineAV = scale(2.0, 6.0, topLineAV);
	float topLineA = box(topLineAV[0], topLineAV[1]);

	vec2 topLeftTrinagleAV = vec2(x, y);
	topLeftTrinagleAV = translate(vec2(5, 21), topLeftTrinagleAV);
	topLeftTrinagleAV = rotate(PI / 2.0, topLeftTrinagleAV);
	topLeftTrinagleAV = scale(2.0, 2.0, topLeftTrinagleAV);
	float topLeftTrinagleA = triangle(topLeftTrinagleAV[0], topLeftTrinagleAV[1]);

	vec2 topRightTrinagleAV = vec2(x, y);
	topRightTrinagleAV = translate(vec2(21, 21), topRightTrinagleAV);
	topRightTrinagleAV = scale(2.0, 2.0, topRightTrinagleAV);
	float topRightTrinagleA = triangle(topRightTrinagleAV[0], topRightTrinagleAV[1]);

	vec2 midRightTrinagleAV = vec2(x, y);
	midRightTrinagleAV = translate(vec2(17, 17), midRightTrinagleAV);
	midRightTrinagleAV = rotate(PI, midRightTrinagleAV);
	midRightTrinagleAV = scale(2.0, 2.0, midRightTrinagleAV);
	float midRightTrinagleA = triangle(midRightTrinagleAV[0], midRightTrinagleAV[1]);

	vec2 midLeftTrinagleAV = vec2(x, y);
	midLeftTrinagleAV = translate(vec2(9, 17), midLeftTrinagleAV);
	midLeftTrinagleAV = rotate((3.0 * PI) / 2.0, midLeftTrinagleAV);
	midLeftTrinagleAV = scale(2.0, 2.0, midLeftTrinagleAV);
	float midLeftTrinagleA = triangle(midLeftTrinagleAV[0], midLeftTrinagleAV[1]);

	return 
        max(leftLegA,
		max(rightLegA,
		max(midLineA,
		max(topLineA,
		max(topLeftTrinagleA,
		max(topRightTrinagleA,
		max(midLeftTrinagleA,
		midRightTrinagleA)))))));
}

float drawSh(float x, float y)
{
 	vec2 leftLegShV = vec2(x, y);
	leftLegShV.xy = translate(vec2(-21, 13), leftLegShV);
	leftLegShV.xy = scale(2.0, 10.0, leftLegShV);
	float leftLegSh = box(leftLegShV[0], leftLegShV[1]);

	vec2 rightLegShV = vec2(x, y);
	rightLegShV.xy = translate(vec2(-5, 13), rightLegShV);
	rightLegShV.xy = scale(2.0, 10.0, rightLegShV);
	float rightLegSh = box(rightLegShV[0], rightLegShV[1]);

	vec2 midLegShV = vec2(x, y);
	midLegShV.xy = translate(vec2(-13, 15), midLegShV);
	midLegShV.xy = scale(2.0, 8.0, midLegShV);
	float midLegSh = box(midLegShV[0], midLegShV[1]);

	vec2 bottLineShV = vec2(x, y);
	bottLineShV.xy = translate(vec2(-13, 5), bottLineShV);
	bottLineShV.xy = rotate(PI / 2.0, bottLineShV);
	bottLineShV.xy = scale(2.0, 6.0, bottLineShV);
	float bottLineSh = box(bottLineShV[0], bottLineShV[1]);


	return 
        max(leftLegSh,
		max(rightLegSh,
		max(midLegSh,
		bottLineSh))); 
}

float drawO(float x, float y)
{
	vec2 leftLegOV = vec2(x, y);
	leftLegOV.xy = translate(vec2(-21, -13), leftLegOV);
	leftLegOV.xy = scale(2.0, 6.0, leftLegOV);
	float leftLegO = box(leftLegOV[0], leftLegOV[1]);

	vec2 rightLegOV = vec2(x, y);
	rightLegOV = translate(vec2(-5, -13), rightLegOV);
	rightLegOV = scale(2.0, 6.0, rightLegOV);
	float rightLegO = box(rightLegOV[0], rightLegOV[1]);

	vec2 butLineOV = vec2(x, y);
	butLineOV = translate(vec2(-13, -21), butLineOV);
	butLineOV = rotate(PI / 2.0, butLineOV);
	butLineOV = scale(2.0, 6.0, butLineOV);
	float butLineO = box(butLineOV[0], butLineOV[1]);

	vec2 topLineOV = vec2(x, y);
	topLineOV = translate(vec2(-13, -5), topLineOV);
	topLineOV = rotate(PI / 2.0, topLineOV);
	topLineOV = scale(2.0, 6.0, topLineOV);
	float topLineO = box(topLineOV[0], topLineOV[1]);

	vec2 topLeftTrinagleOV = vec2(x, y);
	topLeftTrinagleOV = translate(vec2(-21, -5), topLeftTrinagleOV);
	topLeftTrinagleOV = rotate(PI / 2.0, topLeftTrinagleOV);
	topLeftTrinagleOV = scale(2.0, 2.0, topLeftTrinagleOV);
	float topLeftTrinagleO = triangle(topLeftTrinagleOV[0], topLeftTrinagleOV[1]);

	vec2 topRightTrinagleOV = vec2(x, y);
	topRightTrinagleOV = translate(vec2(-5, -5), topRightTrinagleOV);
	topRightTrinagleOV = scale(2.0, 2.0, topRightTrinagleOV);
	float topRightTrinagleO = triangle(topRightTrinagleOV[0], topRightTrinagleOV[1]);

	vec2 botLeftTrinagleOV = vec2(x, y);
	botLeftTrinagleOV = translate(vec2(-21, -21), botLeftTrinagleOV);
	botLeftTrinagleOV = rotate(PI, botLeftTrinagleOV);
	botLeftTrinagleOV = scale(2.0, 2.0, botLeftTrinagleOV);
	float botLeftTrinagleO = triangle(botLeftTrinagleOV[0], botLeftTrinagleOV[1]);

	vec2 botRightTrinagleOV = vec2(x, y);
	botRightTrinagleOV = translate(vec2(-5, -21), botRightTrinagleOV);
	botRightTrinagleOV = rotate(3.0*PI/2.0, botRightTrinagleOV);
	botRightTrinagleOV = scale(2.0, 2.0, botRightTrinagleOV);
	float botRightTrinagleO = triangle(botRightTrinagleOV[0], botRightTrinagleOV[1]);

	return 
		max(leftLegO,
		max(rightLegO,
		max(butLineO,
		max(topLineO,
		max(topLeftTrinagleO,
		max(topRightTrinagleO,
		max(botLeftTrinagleO,
		botRightTrinagleO)))))));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float wmax=max(iResolution.x,iResolution.y);
    float aspect =iResolution.y/iResolution.x;
    vec2  vnorm = vec2(wmax,wmax);   
    vec2 uv = fragCoord/vnorm ; //iResolution.xy;
    float x=uv.x;
    float y=uv.y;

    x *=100.0;
    y *=100.0;

    x -=50.0;
    y -=50.0*aspect;


    float grid =  max(grid(x,y), axes(x,y))/2.0;

    float f = drawO(x,y)/10.0;
    f = max(f, drawSh(x,y)/10.0);
    f = max(f, drawA(x,y)/10.0);
    f = 0.01/f; 

    // Time varying pixel color

    vec3 col = vec3(0.0,grid,0.0);

    if(f >= 0.0)col.x =  f;//+cos(iTime);// + 0.5*cos(iTime*iTime/1000.0+uv.xyx+vec3(10,2,4));
    else      col.z = -f;
    // Output to screen
    fragColor = vec4(col,1.0);


}
