// Fork of "FrameStella" by gaz. https://shadertoy.com/view/3llBzN
// 2020-08-08 14:15:50

#define rot(a) mat2(cos(a), sin(a), -sin(a), cos(a))

float deFrameStella(vec3 p){
    p = abs(p)-1.;
    if (p.x < p.z) p.xz = p.zx;
    if (p.y < p.z) p.yz = p.zy;
    if (p.x < p.y) p.xy = p.yx;
    return length(cross(p,normalize(vec3(0,1,1))))-0.05;
}

float map(vec3 p){
    return deFrameStella(p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
	vec3 p,
	ro=vec3(0,0,-4),
	rd=normalize(vec3(uv,2));
	float h=0.,d,i,zoom = 1.1;
	ro*=zoom;
	for(i=1.;i<50.;i++){
		p=ro+rd*h;
		p/=zoom;
		//p.xy*=rot(iTime*.6);
		//p.yz*=rot(iTime*0.2);
		p.zy*=rot(iTime*0.1);
		d=map(p);
		if(d<0.0001)break;
		h+=d;
	}
	fragColor.xyz+=10./i;  
}
