const float PI = 3.141592;

float circle (vec2 pos) {
    float x = 5.*(sin(iTime) + 1.2);
    if(length(pos) > 1.3 * x) {
    	return length(pos.x * pos.y * pos);
    }

    if(length(pos) > 1.0 * x) {
    	return length(vec2(pow(pos.x,2.), pos.y) * 3. * sin(iTime));
    }

    if(length(pos) > 0.7 * x) {
    	return length(pos * sin(iTime));
    }

    if(length(pos) > 0.4 * x) {
    	return length(pos) - 0.5;
    }

    return length(pos/2.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    float w = 0.05*sin(iTime/10.);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 pos = fragCoord/iResolution.xy * 2. - 1.;
    pos.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(step(0.5, circle(pos)));

    vec3 color = vec3(step(w/2., mod(circle(pos),w)));

    // Output to screen
    fragColor = vec4(color,1.0);


}
