void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec4 trail = texture(iChannel0, uv);

    // Output to screen
    fragColor = trail;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Trail tail length
    float uLength = 0.95;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 point = iMouse.xy/iResolution.xy;

    uv.x *= iResolution.x/iResolution.y;
    point.x *= iResolution.x/iResolution.y;

    float dist = clamp(1. - distance(point, uv)/ 0.15, 0.0, 1.0);

    vec3 color = texture(iChannel0, fragCoord/iResolution.xy).rgb * uLength;

    color += dist;

    //color = clamp(color, vec3(0.0), vec3(1.0));

    fragColor = vec4(color, 1.0);
}
