void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord / iResolution.xy;

    vec3 col = vec3(0.0);

    uv -= 0.5;
    uv *= 10.;

    uv.y *= .1;    

    uv.x /= tan(uv.y) * (iResolution.x /1.6) /  tan(uv.x * (iTime + 1000.0) / 10.2) - uv.x * (iResolution.x / 35.);     

    col -=  step(1., abs(uv.y)) - step(uv.y, abs(uv.y));

    col.r = uv.x / cos(sin(iTime));

    col.g *= length(uv.x);

    col.b = sin(uv.x) * (cos(iTime) * 3.0);

    fragColor = vec4(col, 1.0);
}
