void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( u+u - R ) / R.y;
    float l = length(U),                             // polar coords
          a = atan(U.y,U.x); a = mod(a,6.283);

    a += 10.*exp(-4.*l);                             // roll

    O = vec4 ( -sin(a) );                            // wave mask

    U = l * vec2(cos(a),sin(a));                     // wave text. coords

 // U   -= .2*iTime;                                 // variant 1
 // U.y -= .2*iTime; U.x /= a;                       // variant 2
    U.y -= .2*iTime; U.x /= a; U.x -= .07*iTime;     // variant 3

    O *= texture(iChannel0,U)*1.2 + vec4(0,.2,.5,0); // appearance
}
