float map(vec3 p){
    p=mod(p,2.) *0.6;
    p = abs(p)-0.6;
    if (p.x < p.z) p.xz = p.zx;
    if (p.y < p.z) p.yz = p.zy;

    float s=0.45;


    return length(p)/s;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv=(2.*fragCoord-iResolution.xy)/iResolution.y;
	vec3 p,
  		ro=vec3(.4+.32*sin(iTime*.155),.2+.05*cos(iTime*.05),-.5*iTime),
  		w=normalize(vec3(.2,sin(iTime*.05),-1)),
  		u=normalize(cross(w,vec3(1,5,5))),
  		rd=mat3(u,cross(u,w),w)*normalize(vec3(uv,2));
  	float h=0.1,d,i,zoom = 2.;
 	ro*=zoom;
	for(i=2.;i<911250.;i++){
    	p=ro+rd*h;
    	p/=zoom;
	  	d=map(p);
    	if(d<0.1||h>15.)break;
    	h+=d;
	}
	fragColor.xyz=15.*vec3(cos(p*.73)*.643335+1.15)/i;
}
