void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    vec2 u = uv-.5;
    u*=3.;
    float  k = atan(acos(u.x),cos(u.y))*.5;    
    vec2 c = u;
    c.x-=1.1;
    c.x+=mod(iTime*0.2,0.2)*20.4-1.0;

    float d = length(c*1.0);
    float e  = step(u.x,1.);
    k += smoothstep(d,d*1.04,0.5)*e;
    float r = acos(uv.y)*0.8;
    k*=r; 
    vec3 g = vec3(k);
    fragColor = vec4(g,1.0);
}
