void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float asp = iResolution.x/iResolution.y;

	uv-=0.5;
    uv.x*=asp;
    //uv.y+=0.2;
    //uv.y-=sqrt(abs(uv.x))*(sin(iTime/2.)/2.+0.5);
    /// Time varying pixel color
    uv.x+=sin(iTime)*0.5;
    float z = sqrt(sqrt(0.2*0.2*0.2*0.2-uv.x*uv.x*uv.x*uv.x-uv.y*uv.y*uv.y*uv.y))/0.2;
    //uv.y+=sin(iTime)*0.5+0.5;
    uv.x-=sin(iTime)*0.5;
    float c = (sqrt(pow(0.3*0.3,sin(iTime)/2.+1.)-pow(uv.x*uv.x,sin(iTime)/2.+1.)-pow(uv.y*uv.y,sin(iTime)/2.+1.)))/(0.3-sin(iTime)*0.1);
   uv*=2.5;
    uv.x/=asp;
    float x =-uv.y;
    float y =uv.x;

    x+=(sin(iTime)*0.5);
    y+=(cos(iTime)*0.5);

    x*=2.;y*=2.;
     y*=asp;
    vec2 dis = vec2(0.4,0.4);
    dis+=sin(iTime)/2.+0.1;

     float b =((1.-sign(-x-.9+abs(y*2.)))/3.*(sign(.9-x)+1.)/3.)*(sign(x+.65)+1.)/2. - ((1.-sign(-x-.39+abs(y*2.)))/3.*(sign(.9-x)+1.)/3.) + ((1.-sign(-x-.39+abs(y*2.)))/3.*(sign(.6-x)+1.)/3.)*(sign(x-.35)+1.)/2.;
	z = max(b,z);
    z=max(c,z);
    // Output to screen
    fragColor += z;
}
