void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0, uv);
}

float minFade = 0.75;
float maxFade = 1.00;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float percent = iMouse.x / iResolution.x;
    float fadeAmount = mix(minFade, maxFade, percent);

    vec4 prevImg = texture(iChannel0, uv);
    vec4 video = texture(iChannel1, uv);
    fragColor = mix(video, prevImg, fadeAmount);
}
