const float radius = .0625;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 r = 2.*vec2(fragCoord.xy - .5 * iResolution.xy) / iResolution.y;


	vec2 xy = fragCoord.xy / iResolution.xy;
    vec4 final = texture(iChannel0, xy);

    vec2 mouse = iMouse.xy / iResolution.xy;
	vec4 mColor = vec4(0.,0.,0.,1.);

    float circ = r.x * r.x * mouse.x * mouse.x + r.y * r.y * mouse.y * mouse.y;

	if (circ < radius * radius)
        final = mColor;


    fragColor = final;
}
