mat2 ro (float a) {
	float s = sin(a),c = cos(a);
    return mat2(c,-s,s,c);
}

void mainImage( out vec4 Q, in vec2 u )
{
    R = iResolution.xy;
    vec3 mi = 0.5*vec3(R/N,N*N);
    vec3 p = vec3(0,0,-.9*R.x/N);
    vec3 d = normalize(vec3((u-0.5*R)/R.y,1));
    if (iMouse.z>0.&&iMouse.x<0.9*R.x) {
 		p.xz *= ro(6.2*iMouse.x/R.x);
		d.xz *= ro(6.2*iMouse.x/R.x);
        p.yz *= ro(6.2*iMouse.y/R.y);
		d.yz *= ro(6.2*iMouse.y/R.y);
    } else {
		p.xz *= ro(.2*iTime);
		d.xz *= ro(.2*iTime);
        p.zy *= ro(.3*iTime);
		d.zy *= ro(.3*iTime);
	}
    Q = vec4(0);
    for (int i = 0; i < 50; i++) {
        vec3 o = abs(p)-mi;
        float m = length(max(o,0.));
        if (m<.1)
        { 	
            vec4 a = s3d(iChannel0,p+mi);
            Q += .1*abs(a);
            p += 3.*d*exp(-7.*(length(a.xyz)));
        } else p += d*max(o.x,max(o.y,o.z));

 	} 
    Q = .7*atan(Q);
}

#define N 8.
vec2 R;
vec2 d2 (vec3 U) {
    U = mod(U,vec3(R/N,N*N));
	return U.xy+vec2(mod(U.z,N),floor(U.z/N))*R/N;
}
vec3 d3 (vec2 u) {
    vec2 o = floor(u/R*N);
	return vec3(mod(u,R/N),o.x+o.y*N);
}
vec4 s3d (sampler2D T,vec3 U) {
    vec3 U0 = vec3 (U.xy,floor(U.z)),
         U1 = vec3 (U.xy, ceil(U.z));
    vec4 o = mix(
        texture(T,d2(U0)/R),
        texture(T,d2(U1)/R),
        fract(U.z)
    );
    if (U.x < 1. || R.x/N-U.x < 1. ||
        U.y < 1. || R.y/N-U.y < 1. ||
       	U.z < 0. || U.z > N*N) o.xyz*=0.;
    return o;
}
#define A(U) texture(iChannel0, d2((U))/R)
#define B(U) texture(iChannel1, d2((U))/R)
#define C(U) texture(iChannel2, d2((U))/R)
#define D(U) texture(iChannel3, d2((U))/R)

void mainImage( out vec4 Q, in vec2 u )
{
    R = iResolution.xy;
    vec3 U = d3(u);
    Q = A(U);
    vec4 b = B(U);
    vec4 dm = 1./6.*(
    	A(U+vec3(1,0,0))+
        A(U+vec3(0,1,0))+
        A(U+vec3(0,0,1))+
        A(U-vec3(1,0,0))+
        A(U-vec3(0,1,0))+
        A(U-vec3(0,0,1))
    )-Q;
    Q += b + .01*dm;
    if (iFrame < 1) Q = sin(length(U-0.5*vec3(vec3(R/N,N*N)))*vec4(1,2,3,4));
	if (length(U-0.5*vec3(R/N,N*N)+7.*sin(iTime*vec3(1,2,3)))<1.5) Q = sin(5.*iTime*vec4(1,2,3,4));
}

void mainImage( out vec4 Q, in vec2 u )
{
    R = iResolution.xy;
    vec3 U = d3(u);
    Q = B(U);
    vec4 a = A(U);
    vec4 c = C(U);
    vec4 dm = 1./6.*(
    	A(U+vec3(1,0,0))+
        A(U+vec3(0,1,0))+
        A(U+vec3(0,0,1))+
        A(U-vec3(1,0,0))+
        A(U-vec3(0,1,0))+
        A(U-vec3(0,0,1))
    )-a;
    vec4 dmb = 1./6.*(
    	B(U+vec3(1,0,0))+
        B(U+vec3(0,1,0))+
        B(U+vec3(0,0,1))+
        B(U-vec3(1,0,0))+
        B(U-vec3(0,1,0))+
        B(U-vec3(0,0,1))
    )-Q;
    Q += dm + .01*dmb;
    if (length(a.xyz)>0.) Q.xyz -= .03333*a.xyz/length(a.xyz)+.02*a.xyz/sqrt(dot(c.xy,c.xy)+dot(a.xyz,a.xyz));
}

