void mainImage( out vec4 Q, in vec2 u )
{
    R = iResolution.xy;
    vec3 U = d3(u);
    Q = D(U);
    vec4 a = A(U);
    vec4 c = C(U);
    vec4 dm = 1./6.*(
    	C(U+vec3(1,0,0))+
        C(U+vec3(0,1,0))+
        C(U+vec3(0,0,1))+
        C(U-vec3(1,0,0))+
        C(U-vec3(0,1,0))+
        C(U-vec3(0,0,1))
    )-c;
    vec4 dmd = 1./6.*(
    	D(U+vec3(1,0,0))+
        D(U+vec3(0,1,0))+
        D(U+vec3(0,0,1))+
        D(U-vec3(1,0,0))+
        D(U-vec3(0,1,0))+
