void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    uv.x = fract(uv.x)-0.5;

    float d = length(uv*vec2(0.12,0.25))*80.-iTime*2.;
    float e = length(uv)*sin(iTime/6.0)*8.-iTime*2.;

    d = pow(min(sin(d),cos(d)),sin(d)*2.);
    e = pow(min(sin(e),cos(d)),cos(e)*2.);
    d = max(d,e);

    d = smoothstep(sin(d),e,sin(d)+cos(e));
    e = step(0.5,cos(e));
    vec3 col = vec3(d/2.,1.-e,d);

    // Output to screen
    fragColor = vec4(col,1.0);
}
