// visualize
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.*fragCoord/iResolution.xy-1.;
	uv.x += iTime;
    uv *= 1.;

    float v = fbm(uv.x);

    v = .01/abs(uv.y+v); // graph

    vec3 col = vec3(0,v*v,0);
    fragColor = vec4(col, 1);
}

#define e float(1.)
// from: https://www.shadertoy.com/view/4djSRW
float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float noise1d(float p)
{
    float i = floor( p );
    float f = fract( p );

	float u = f*f*(3.0-2.0*f);

    u = mix( hash11(i)*f, hash11(i+1.)*(f-1.), u);

    u *= cos(p);
    return pow(u, e)*exp2(e*2.);
}

float fbm( float p )
{
    return noise1d(    p)+
        .5*noise1d(2. *p)+
       .25*noise1d(4. *p)+
      .125*noise1d(8. *p)+
     .0625*noise1d(8. *p)+
    .03125*noise1d(16.*p);
}

vec2 mainSound( in int samp, float time )
{
    return vec2(  fbm(55.0*time+fbm(time*.125)*4. )  )
        *.75*smoothstep(0., 1., time*.5);
}
