// inspired by wj's https://shadertoy.com/view/wtXczf

void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy,
         U = u - vec2(R.y/2.,0) ;
    O +=    fract(U.x*U.y/R.y)
         * ( abs(u.x-R.y)<R.y/2.?1.:.5 ); }
