// Reference: https://iquilezles.org/articles/distfunctions

#define PI 3.1415926535898
#define AA 1   // make this 1 is your machine is too slow
#define RotateCamera true   // Set true then camera rotates

//------------------------------------------------------------------

float sdPlane( vec3 p )
{
	return p.y;
}

float sdSphere( vec3 p, float s, out vec2 uv )
{
    vec3 n = normalize(p);

    uv = vec2(0.5+0.5*atan(p.x, p.z)/(0.5*PI), acos(p.y)/PI);

    return length(p)-s;
}

float sdTorus( vec3 p, vec2 t )
{
    return length( vec2(length(p.xz)-t.x,p.y) )-t.y;
}

float sdTriPrism( vec3 p, vec2 h )
{
    vec3 q = abs(p);
    float d1 = q.z-h.y;
#if 1
    // distance bound
    float d2 = max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5;
#else
    // correct distance
    h.x *= 0.866025;
    float d2 = sdEquilateralTriangle(p.xy/h.x)*h.x;
#endif
    return length(max(vec2(d1,d2),0.0)) + min(max(d1,d2), 0.);
}
float sdCone( in vec3 p, in vec3 c )
{
    vec2 q = vec2( length(p.xz), p.y );
    float d1 = -q.y-c.z;
    float d2 = max( dot(q,c.xy), q.y);
    return length(max(vec2(d1,d2),0.0)) + min(max(d1,d2), 0.);
}
float sdBox( vec3 p, vec3 b, out vec2 uv )
{
    uv = vec2(0,0);
    vec3 d = abs(p) - b;

    vec3 t = 0.5*abs(p - b)/b;

    if(d.x >  d.y && d.x > d.z)
    {
        uv.x = t.y;
        uv.y = t.z;
    }
    else if(d.y > d.z)
    {
        uv.x = t.z;
        uv.y = t.x;
    }
    else
    {
        uv.x = t.x;
        uv.y = t.y;
    }

    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}
//------------------------------------------------------------------


vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}


//------------------------------------------------------------------

vec2 map( in vec3 pos )
{
    vec2 uv;
    //vec2 res = opU( vec2( sdPlane(pos), 1.0 ),
	//                vec2( sdBox(pos-vec3( 0.0, 0.5, 0.0), vec3(0.25), uv),
    //                     21.0 ) );
    vec2 res = vec2(sdBox(pos-vec3( 0.0, 0.5, 0.0), vec3(0.25), uv), 21.0 );

    return res;
}
vec2 map( in vec3 pos, out vec2 uv )
{

    float r = 0.25;
//    vec2 res = opU( vec2( sdPlane(pos), 1.0 ),
//	                vec2( sdBox(pos-vec3( 0.0, 0.5, 0.0), vec3(0.25), uv),
//                         21.0 ) );
    vec2 res = vec2( sdBox(pos-vec3( 0.0, 0.5, 0.0), vec3(0.25), uv), 21.0 );

    return res;
}

vec2 castRay( in vec3 ro, in vec3 rd, out vec2 uv )
{
    float tmin = 1.0;
    float tmax = 20.0;

#if 1
    // bounding volume
    float tp1 = (0.0-ro.y)/rd.y; if( tp1>0.0 ) tmax = min( tmax, tp1 );
    float tp2 = (1.6-ro.y)/rd.y; if( tp2>0.0 ) { if( ro.y>1.6 ) tmin = max( tmin, tp2 );
                                                 else           tmax = min( tmax, tp2 ); }
#endif

    float t = tmin;
    float m = -1.0;
    for( int i=0; i<64; i++ )
    {
	    float precis = 0.0004*t;
	    vec2 res = map( ro+rd*t, uv );
        if( res.x<precis || t>tmax ) break;
        t += res.x;
	    m = res.y;
    }

    if( t>tmax ) m=-1.0;
    return vec2( t, m );
}


float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

// https://iquilezles.org/articles/checkerfiltering
float checkersGradBox( in vec2 p )
{
    // filter kernel
    vec2 w = fwidth(p) + 0.001;
    // analytical integral (box filter)
    vec2 i = 2.0*(abs(fract((p-0.5*w)*0.5)-0.5)-abs(fract((p+0.5*w)*0.5)-0.5))/w;
    // xor pattern
    return 0.5 - 0.5*i.x*i.y;                  
}
vec3 renderFloor(in vec3 ro, in vec3 rd )
{
    vec3 col = vec3(0.7, 0.9, 1.0) +rd.y*0.8;
    vec2 uv;
    vec2 res = castRay(ro,rd, uv);
    float t = res.x;
	float m = res.y;

    vec3 pos = ro + t*rd;
    vec3 nor = calcNormal( pos );
    vec3 ref = reflect( rd, nor );

    // material        
    col = 0.45 + 0.35*sin( vec3(0.05,0.08,0.10)*(m-1.0) );


    float f = checkersGradBox( 5.0*pos.xz );
    col = 0.3 + f*vec3(0.1);


    // lighitng        
    float occ = calcAO( pos, nor );
    vec3  lig = normalize( vec3(0.4, 0.9, 0.5) );
    vec3  hal = normalize( lig-rd );
    float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
    float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
    float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
    float dom = smoothstep( -0.1, 0.1, ref.y );
    float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );

    dif *= calcSoftshadow( pos, lig, 0.02, 2.5 );
    dom *= calcSoftshadow( pos, ref, 0.02, 2.5 );

    float spe = pow( clamp( dot( nor, hal ), 0.0, 1.0 ),16.0)*
        dif *
        (0.04 + 0.96*pow( clamp(1.0+dot(hal,rd),0.0,1.0), 5.0 ));

    vec3 lin = vec3(0.0);

    vec3 kd = vec3(1, 0, 0);
    lin += 1.30*dif*vec3(1.00,0.80,0.55);
    lin += 0.40*amb*vec3(0.40,0.60,1.00)*occ;
    col = col*lin;


	return vec3( clamp(col,0.0,1.0) );
}

vec3 renderObjects(in vec3 ro, in vec3 rd )
{
    vec2 uv; /* texture coordinate */
    vec2 res = castRay(ro, rd, uv);
    float t = res.x;
	float m = res.y;
    vec3 pos = ro + t*rd;


    vec3 n = calcNormal( pos ); /* normal */
    vec3 wo = -rd; /* eye direction */    
    vec3 wi = normalize( vec3(0.4, 0.9, 0.5) ); /* incoming light direction */
    vec3 wr = reflect(-wo, n); /* mirror direction */
    vec3 li = vec3(1.0, 1.0, 1.0);
    vec3 h = normalize(wi + wo);
    vec3 col = vec3(0, 0, 0);
    vec3 kd = vec3(0.01, 0.2, 0.95); /* diffuse coef. */

    /* TODO1: Implement mirror effect using enviroment map. */
    // col += ...

    /* TODO2: Use blurred enviroment map as incident light */
    //li = texture(iChannel1, n).rgb;
    // col += ... 

    /* TODO3: Calc Fresnel term, then apply to env map */
    float eta = 1.5; /* refractive index */
    float F0 = (1.0-eta)*(1.0-eta)/((1.0+eta)*(1.0+eta));
    float F = F0 + (1.0 + F0) * pow(1.0-dot(wi, h), 5.0); /* Fresnel Term (Schlick Approx.) */	
    // col +=  ...    

    return col;

}
vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 col = vec3(0.7, 0.9, 1.0) +rd.y*0.8;
    vec2 uv;
    vec2 res = castRay(ro,rd,uv);
    float t = res.x;
	float m = res.y;
    if( m>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );
        vec3 ref = reflect( rd, nor );

        // material        
        if( m<1.5 ) col = renderFloor(ro, rd);
        else col = renderObjects(ro, rd);
    }
    else
    {
        /* environment map for backgound */
        col = texture(iChannel0, rd).rgb;
    }

	return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mo = iMouse.xy/iResolution.xy;
	float time = 15.0 + iTime;


    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
#else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
#endif

		// camera	        
        vec3 ro = vec3( 0, 1.5, 1.5 );

        /* Camera rotation switch */
        bool cameraRotOn = RotateCamera;

        if(cameraRotOn) ro = vec3( 1.5*sin(0.1*iTime), 1.5, 1.5*cos(0.1*iTime));


        vec3 ta = vec3( 0, 0, 0 );
        // camera-to-world transformation
        mat3 ca = setCamera( ro, ta, 0.0 );
        // ray direction
        vec3 rd = ca * normalize( vec3(p.xy,2.0) );

        // render	
        vec3 col = render( ro, rd );

		// gamma
        col = pow( col, vec3(0.4545) );

        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif


    fragColor = vec4( tot, 1.0 );
}
