float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

float map(vec3 p){
    p=mod(p,2.)-1.;
    p = abs(p)-1.;
    if (p.x < p.z) p.xz = p.zx;
    if (p.y < p.z) p.yz = p.zy;
    if (p.x < p.y) p.xy = p.yx;
    float de=1.0;
    for(float i=0.0;i<1.01;i+=0.1){
        vec3 a=vec3(0,i-1.,0),b=vec3(0,0,-i);
        de=min(de,sdCapsule(p,a,b,0.003));
    }
    return de;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
	vec3 p,
	e0=vec3(5,2,3), e1=vec3(2,3,7),
	d0=vec3(0,1,-1),  d1=vec3(1,0,-1),
	ro=mix(e0,e1,fract(iTime*.1)),
	w=normalize(mix(d0,d1,fract(iTime*.1))),
	u=normalize(cross(w,vec3(0,1,0))),
	rd=mat3(u,cross(u,w),w)*normalize(vec3(uv,2));
	float h=0.,d,i,zoom = 2.5;
	ro*=zoom;
	for(i=1.;i<80.;i++){
		p=ro+rd*h;
		p/=zoom;
		d=map(p);
		if(d<0.001||h>20.)break;
		h+=d;
	}
	fragColor.xyz=25.*vec3(cos(p*1.7)*.5+.5)/i;
}
