float shape_circle(vec2 p, float r) {
  return length(p) - r;
}

float smin(float a, float b, float k) {
  float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
  return mix(b, a, h) - k * h * (1.0 - h);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    // Time varying pixel color
    vec3 col1 = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col = vec3(0);


    float time = iTime *1.2;

    float circle = 111110.;



    for(uint i = 0u; i<150u; i++)
    {

        float rg = sin(float(i) *1.17);
        float rg2 = sin(float(i*11u) *4.17) * cos(float(i*13u) *4.17);


    float h =  mod(iTime + 6.1+ rg2, 2.) -2.- rg;
    vec2 circle1Time = vec2(sin(time*rg*rg2*13. + 0.1*rg) * 0.3, h);
    float circle1Size = mod(time + 3.1, 112.) + 1. +4.7;

        float aux = shape_circle(p - circle1Time, (-h)*1.12/circle1Size);
        circle = smin(aux, circle, 0.626);   



    }

    float f = -circle;
    if(circle < -0.0) col = vec3(f, f*f, f*f*f*f) * 3.;




    // Output to screen
    fragColor = vec4(col,1.0);
}
