void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel0, uv).rgb;
    col = 1.0 - col;
    col = pow(col, vec3(1.0) / 0.5);

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 video_0 = texture(iChannel0, uv).rgb;
    vec3 video_1 = texture(iChannel3, uv).rgb;

    vec3 video_bb = texture(iChannel2, uv - vec2(0.0, -13.750) / iResolution.xy).rgb;

    vec3 col = abs(video_0 - video_1);
    col = mix(video_bb * (1.0 - col) * 1.1, col, step(0.2, length(col)));

    fragColor = vec4(col, 1.0);
}
