// visualize
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.*fragCoord/iResolution.xy-1.;
	uv.x += iTime;
    uv *= 5.;

    float v = abs(voronoi1d(uv.x).x);

    v = .02/abs(uv.y+v); // graph

    vec3 col = vec3(0, v*v, 0);
    fragColor = vec4(col, 1);
}

// from: https://www.shadertoy.com/view/4djSRW
float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

vec2 voronoi1d(float x) {
    float gx = fract(x)-.5;
	float ix = floor(x);
    float id;

    float res = 1e2;

    for(int i=-1; i<=1; i++)
    {
        float o = float(i);
        float n = hash11(ix+o);
        float p = gx-(o+n);
        float d = abs(p);

        if(d<res) {
            res = d;
            id = ix+p;
        }
    }
    res = pow(res, 3.);
    return vec2(res-.5, id);
}

vec2 mainSound( in int samp, float time )
{
    float wav = abs(voronoi1d(440.0*time).x);
        +.5*voronoi1d(440.0*time).xx;
    wav *= smoothstep(0., 1., time*.5); // prevents a surprise bye bye ears moment
    return vec2(wav);
}
