const vec3 msat = vec3(.3,.3,.3);
const float msdiv = .12;
vec3 saturate( vec3 v ) {
  // https://www.shadertoy.com/view/Wtt3R8
  float sv = dot(v,msat);
  vec3 hs = v/sv*msdiv;
  float ls = (1.-exp(-sv))*.9;
  float s = 1.-exp(-sv*sv/10.);
  vec3 hs2 = mix(hs,vec3(1),s);
  return ls*hs2/mix(msdiv,1.,s);
}

vec3 image( sampler2D chan, vec2 uv, vec3 gain )
{
  vec3 v = 1.*pow(texture(chan, uv).rgb,vec3(2.2));
  v+= .1*pow(textureLod(chan, uv, 5.).rgb,vec3(2.2));
  v*=gain;
  return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord/iResolution.xy*vec2(2,2);
  vec3 v = vec3(0);
  float gain = exp2(iMouse.x/100.);
  if (iMouse.x<2.) gain = (4.+2.*cos(iTime))*(1.+pow(.6+.2*sin(iTime*5.)+.1*sin(iTime*7.),2.));
  if      (uv.y>6./3.5) v = (vec3(1.,.2,.04)*fract(uv.x)*gain);
  else if (uv.y>5./3.5) v = image(iChannel0, uv, gain*vec3(1));
  else if (uv.y>4./3.5) v = image(iChannel1, uv, gain*vec3(1));
  else if (uv.y>3./3.5) v = image(iChannel2, uv, gain*vec3(1));
  else if (uv.y>2./3.5) v = image(iChannel1, uv, gain*vec3(0.4,1,0.01));
  else if (uv.y>1./3.5) v = image(iChannel3, uv, gain*vec3(0.04,0.04,2));
  else                  v = image(iChannel3, uv, gain*vec3(1));
  if (uv.x>=1.) v = saturate(v);

#ifdef SHOWCLIPPING
  float clipdet = float(any(greaterThan(v,vec3(1.))));
  if (fwidth(clipdet)>0.) { fragColor = vec4(1.-vec3(greaterThan(v,vec3(1))),0); return; }
  if (clipdet>0. && (int(dot(fragCoord/2.,vec2(1)))%2)==0) v = vec3(0);
#endif
  if (uv.x<1.) v = mix(1.,.6,uv.x)*clamp(v,0.,1.);
  fragColor = pow(vec4(v*255./256.,1.),vec4(1./2.2));
}
