void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

	vec2 st = uv;

    st.x += sin(st.y * 10.0) * 0.5 + 0.5;

    st.x -= 0.5;
    st.x /= (((1.0 - uv.y) - 0.5) * 2.0) * 5.0;


    vec2 vz = st;

    vec3 col = vec3(0.0);

    vec3 pink = vec3(0.7, 0.2, 0.3) * 1.3;
    vec3 blu = vec3(0.23, 0.29, 0.31) * 1.0;

    const float samples = 5.0;
    st.x = st.x * samples;
    float index = floor(st.x);
    st.x = mod(st.x, 1.0);

    st.x -= 0.5;
    st.x *= 2.5;
    st.x += sin(st.y * 5.0 + (index * 2.0 + iTime)) * 0.5 + 0.5;

    col = mix(pink, blu, st.x);

    vz.x = floor(mod(vz.x * 100.0, 2.0));
    col *= vec3(vz.x);

    vec3 gradient = pink * pow((1.0 - abs(uv.y * 2.0 - 1.0)), 2.0);
    col = max(col, gradient);

    // Output to screen
    fragColor = vec4(col,1.0);
}
