vec2 N(float angle) {
    return vec2(sin(angle), cos(angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;
    vec2 mouse = iMouse.xy / iResolution.xy;

    vec3 col = vec3(0);

    uv *= 1.25;
    uv.x = abs(uv.x);
    uv.y += tan((5./6.)*3.1415) * .5;
    vec2 n = N((5./6.)*3.1415);
    float d = dot(n, uv-vec2(.5, 0.));
    uv -= n*max(0., d)*2.;

    //col += smoothstep(.01, .0, abs(d));

  	n = N(mouse.y*(2./3.)*3.1415);

    uv.x += .5;
    float scale = 1.;
    for (int i=0; i<10; i++) {
        uv *= 3.;
        scale *= 3.;
        uv.x -= 1.5;

        uv.x = abs(uv.x);
        uv.x -= .5;
        uv -= n*min(0., dot(n, uv))*2.;
    }

    d = length(uv - vec2(clamp(uv.x, -1., 1.), .0));
    //col += smoothstep(1./iResolution.y, .0, d/scale);
	uv /= scale;
    col += texture(iChannel0, uv*2.-iTime*.03).rgb;

    fragColor = vec4(col,1.0);
}
