float hpo (float i){ //half plus one
	return i*0.5 + 1.;
}
float pi = 3.14159265358979;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;
	uv = uv /1.5;
    float displacement = hpo(hpo(sin(uv.x*20.+iTime))+hpo(cos(uv.y*10.-iTime)))/10.;
    //uv = uv *pi;
    uv = uv + displacement*max(0.,iTime/20.-.2);
    //uv = uv *3.;
	float c = mod(floor(uv.x*10.)+floor(uv.y*10.),2.); //checkerboard
    // Output to screen
    fragColor = vec4(c);
}
