vec2 surfacePosition;


float saturate(float x)
{
    return clamp(x,0.,1.);
}


vec4 tex(vec2 u){
	vec2 p=fract(fract(u)+(sin(u.y+iTime)/4.));

	float f=1.-saturate((max(length(p-vec2(0.25,0.5)),length(p-vec2(0.75,0.5)))-.5)*50.);
    float f2,f3;

	f-=1.-saturate((length(p-.5)-(((sin(iTime+u.x)+2.)/3.)*.25))*50.);
    f2=fract( sin(iTime+u.x)+2. )-f;
    f3= fract(sin(iTime+u.x)*0.4 )-f;


	//return vec4(f,f,f,0.);
    return vec4(f,f2,f3,0.);
}

vec4 tex2( vec2 g )
{
    g /= 10.;
    float color = sign((mod(g.x, 0.1) - 0.05) * (mod(g.y, 0.1) - 0.05));

    float color1, color2;

     color1 = (mod(g.y, 0.3) - 0.07);
    color2 = (mod(g.x, 0.2) - 0.5);
    //return sqrt(vec4(color));
     return sqrt(vec4(color, color1, color2,1.0));
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    	//surfacePosition=fragCoord;
        //vec2 uv = surfacePosition * 2.;

    vec2 uv =fragCoord.xy/iResolution.xy-vec2(0.5,0.5);

    float t = iTime * .5;
    //uv.y += sin(t) * .5;
    //uv.x += cos(t) * .5;
    float a = atan(uv.x,uv.y)/1.57;
    float d = max(max(abs(uv.x),abs(uv.y)), min(abs(uv.x)+uv.y, length(uv)));

    vec2 k = vec2(a,0.8/d + t);

    vec4 tx = tex(k*6.);
    vec4 tx2 = tex2(k*2.);

    // ground
    fragColor = tx2;

    // wall
    if (d<=abs(uv.x)+0.05||d<=abs(uv.x)+uv.y)
        fragColor = tx;

    fragColor *= d;
	fragColor.a = 1.;

}
