#define ANGLE 80.0

float startX = 0.0;
float thresholdX = 0.1;
float blur = 0.05;
float posInterval = 0.02;
float alphaFactor = 0.8;

float colFactor(vec2 uv, float thresholdX, float blur){
    float intervalY = uv.y;
    float tempX = intervalY * cos(radians(ANGLE)) + startX;
    float intervalX = uv.x - tempX;
    return smoothstep(thresholdX, thresholdX - blur, abs(intervalX)) * alphaFactor;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    startX = tan(iTime);
    float factor = colFactor(uv, thresholdX, blur);
    fragColor = factor * vec4(1.0, 0.92, 0.92, 1.0) + vec4(0.0, 0.0, 0.9, 1.0 ) * (1.0 - factor);
}
