vec4 gammaCorrect(vec4 pixel)
{
    pixel = pow(pixel,vec4(2.2));

    return pixel;
}

vec3 marchNotTheMonth(vec3 s, vec3 d)
{

    float h = s.y;
    float a = -d.y;

    float t = h/a;


    vec3 p = s+d*t;

    if(t > 0.)
        {
            vec2 texUv = p.xz;

            vec3 firstMix = vec3(gammaCorrect(texture(iChannel0, texUv)));
            vec3 secondMix = vec3(gammaCorrect(texture(iChannel2, texUv)));

            vec3 thing = mix(firstMix, secondMix, clamp(texUv.x+sin(texUv.y),0.,1.));

            return(thing);
        }

    return vec3(gammaCorrect(texture(iChannel1,d)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 yo = uv*2.-1.;
    vec3 dir = vec3(yo,1);
    vec3 camera = vec3(1,1,iTime);
    vec3 march =  marchNotTheMonth(camera,dir);

    // Time varying pixel color
    vec3 col = vec3(march);


    // Output to screen
    fragColor = vec4(pow(col,vec3(1./2.2)),1);
}
