#define N 32   // correlation window size

//#define T(U)   texelFetch(iChannel0, ivec2(U*1.), 0).x           // pixel precision
//#define T(U)   texture(iChannel0, (U)/R ).x                      // fit to screen
  #define T(U)   smoothstep(0.,1., texture(iChannel0, (U)/R ).x )  // amplify contrasts
//#define T(U) ( texture(iChannel0, (U)/R ) - texture(iChannel0, (U+1.)/R ) ).x // analyse Sobel

void mainImage( out vec4 O, vec2 U )
{
    U -= .5;
    vec2 M = iMouse.xy, P,
         R = iResolution.xy; 
    if (length(M)<15.) M = floor( R*(.5 + .3*cos(iTime+vec2(0,11))) );

    float m0=0., m1=0., m01=0., m00=0., m11=0., c, v0, v1, v01, n2=float(N*N);
    for (int k=0; k < N*N; k++) {               // compute all moments around M,U and cross
        P = vec2(k%N,k/N) - float(N+1)/2.;
        v0   = T(M+P);                          // texture around mouse
        v1   = T(U+P);                          // texture around pixel
        m0  += v0;                              // E(X0)
        m1  += v1;                              // E(X1)
        m01 += v0*v1;                           // E(X0.X1)
        m00 += v0*v0;                           // E(X0²)
        m11 += v1*v1;                           // E(X1²)
    }
    m0/=n2; m1/=n2; m01/=n2; m00/=n2; m11/=n2;  // moments of order 1 & 2
    v0  = m00 - m0*m0;                          // variance X0
    v1  = m11 - m1*m1;                          // variance X1
    v01 = m01 - m0*m1;                          // covariance
    c   = v01 / sqrt(v0*v1);                    // correlation

    O = vec4( c , .3*T(U), -c, 1 ) 
        + pow(max(0.,c),8.); // emphase maximum. variant: abs(c)

    U = abs(U-M) - floor(float(N)/2.*R.y/iChannelResolution[0].y);  // draw stat window around mouse
    if (U.x*U.y==0. && ( U.x<0. || U.y<0.) ) O +=.3;
}
