vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float mandel(vec2 pos, vec2 c) {

    vec2 z = pos;
    for(int i = 0; i < 1000; i++) {
        z.xy = vec2(z.x*z.x-z.y*z.y,z.x*z.y*2.0f) + c;
    }

    return length(z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 u = fragCoord/iResolution.x;
	vec3 col = vec3(0.0f,0.0f,0.0f);
    vec2 uv = (fragCoord-vec2(iResolution.x/4.0f,iResolution.y/2.0f))/iResolution.x*8.0f;
    if(u.x < 0.5) {
        uv.x += -1.0f;
        col = hsv2rgb(vec3((mandel(uv,uv)+iTime)/10.0f,1.0f,1.0f));
    } else {
        uv.x += -4.0f;
        vec2 mouse = (iMouse.xy-vec2(iResolution.x/4.0f,iResolution.y/2.0f))/iResolution.x*8.0f;
        mouse.x += -1.0f;

        col = hsv2rgb(vec3((mandel(uv,mouse)+iTime)/10.0f,1.0f,1.0f));
    }

    fragColor = vec4(col,1.0);

}
