void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
  float t = iTime;
  uv = uv * 2.0 - 1.0;
  vec2 puv = vec2(length(uv.xy), atan(uv.x, uv.y));
  vec4 col = texture(iChannel0, vec2(log(puv.x) - t / 5.0, puv.y / 3.1415926 )) * vec4(0.1, 0.1, 0.9, 1.0);
  float glow = (1.0 - puv.x) * (0.5 + (sin(t * 3.) + 2.0 ) / 4.0);
  // blue glow
  col += vec4(118.0/255.0, 144.0/255.0, 219.0/255.0, 1.0) * (0.4 + glow * 1.0);
  // white glow
  col += vec4(0.2) * smoothstep(0.0, 2.0, glow * glow);
    fragColor = col;
}
