void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	//uv += 0.5;
    uv *= 30.0;

    vec3 a = 0.5 * sin(iTime + uv.xxx);
 	vec3 b = 0.5 * sin(iTime + uv.yyy);

    vec3 c = 0.5 * sin(iTime + uv.xyy); //similar to e
    vec3 e = 0.5 * sin(iTime + uv.yxx);

	vec3 d = 0.5 * sin(iTime + uv.xxy); //similar to g
    vec3 g = 0.5 * sin(iTime + uv.yyx);

    vec3 f = 0.5 * sin(iTime + uv.yxy); //similar to h
    vec3 h = 0.5 * sin(iTime + uv.xyx);

    //adding: a+b, c+e, d+g, f+h give same result
    //similarly for subtracting the groups, although diff for each group
    //multiplying, same as addition
    //dividing, same as subtraction

    //b, c, d, f
    //a, e, g, h

    // Output to screen
    fragColor = vec4(2.0*c-2.0*e,1.0);
}
