void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    // Output to screen
    float x16 = float(int(uv.x * 16.0)) / 16.0;
	float prealpha = float(int(uv.x * uv.x * 16.0)) / 16.0;
	float not_prealpha = x16 * x16;
    float y = uv.y;
	float level = 3.0;
    if (y<1.0/level)
        fragColor = vec4(prealpha, 0.0, 0.0, 1.0);
    else if (y<2.0/level)
        fragColor = vec4(not_prealpha, 0.0, 0.0, 1.0);
    else
        fragColor = vec4(uv.x * uv.x, 0.0, 0.0, 1.0);
}
