void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    float d = length(uv);
    float r = 0.3;
    float col = 0.0f;
    vec3 color = vec3(1, 0, 1);

    col = smoothstep(r, r - 0.01, d);

    // Output to screen
    fragColor = vec4(vec3(col) * color, 1.0);
}
