void mainImage( out vec4 O,  vec2 u )
{
    vec2 R = iResolution.xy;
    u -= .5* R;

    float x = 13.* u.x/R.x;
    O = vec4( cos( x * u.y + 0.*iTime) );
    if (u.y<0.) O = vec4( cos( exp(20.*length(u/R.x)) ) );

    if ( u.x > 0.) O /= fwidth(O);           // right: normalize to get B/W strips

    O = sqrt(.5+.5*O);                     // to sRGB

    if (floor(u.x)==0.) O = vec4(1,0,0,1); // vertical separator
    if (floor(u.y)==0.) O = vec4(0,1,0,1); // horizontal separator
}
