float image(vec2 uv, float offset)
{
    float time = iTime * 4.;
    float y = sin(uv.x * 40. + time) + sin(uv.x * 20. + time);
    y *= pow(1. - abs(uv.x), 3.) * 0.7;
    return abs(uv.y * 8. - y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
	uv.x *= iResolution.x/iResolution.y;

	float c = image(uv + vec2(0.,0.),0.) + abs(uv.x * 1.5);

    fragColor = vec4(0.,1.-c,1.-c,1.);
}
