void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy - .5) / vec2(iResolution.y / iResolution.x, 1.0);
    uv *= 10.;
    vec2 ouv = uv;
    float c = cos(.785);
    float s = sin(.785);
    uv *= mat2(c, -s, s, c);
    // Time varying pixel color
    vec2 guv = fract(uv*5.)-.5; // grid uv
    vec2 id = floor(uv*5.); // grid uv
    float circle = 0.;
    float g = (ouv.y + (sin(iTime/2.)+1.)*45. + 5.) / 10.;

    for(float y = -1.0; y <= 1.; y++)
    for(float x = -1.0; x <= 1.; x++)
    {
        vec2 off = vec2(x, y);
        float d = length(guv - off);
        float dist = length(id + off) * 0.30;
        float r = mix(.3, 0.5, g);
        circle += smoothstep(r,r*.9,d);
    }
    vec3 col = mix(vec3(1.0,.1,.5), vec3(.1,.1,.5), mod(circle, 2.0));
    //col += vec3(1, 0, 0) * (mod(circle+1., 2.0));
    // Output to screen
    fragColor = vec4(col,1.0);
}
