float distCircle(vec2 uv, vec2 pos, float size) {
    float d = length(uv - pos);
    return smoothstep(size, size-0.03, d);
}
float edge(vec2 uv, vec2 pos, float size) {
    uv /= size;
    float d = length(uv - pos);
    float m = 0.3/d - abs(uv.x * uv.y)*50.;
    m -= d*1.;
    m = clamp(0.,1.,m);
    float sub = 0.;
    float r = 0.215;
    float m1 = distCircle(uv, vec2(-sub,-sub), r);


    return m - m1;
}

vec3 halo(vec2 uv) {
    vec3 col = vec3(0);
     col.r += edge(uv, vec2(0), 0.95)*1.1;
     col.g += edge(uv, vec2(0), 1.);
     col.b += edge(uv, vec2(0), 1.05)*0.8;
     return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);

    col += halo(uv);

    fragColor = vec4(col,1.0);
}
