float rand(vec2 n) 
{ 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p)
{
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

// power smooth min from iq;
float smin( float a, float b, float k )
{
    a = pow( a, k ); b = pow( b, k );
    return pow( (a*b)/(a+b), 1.0/k );
}

float circle(in vec2 p) 
{
	vec2 q = p;
    float d = 0.0;

    p*=1.2;
    p.y -=0.3;
    d = length(p);

    // drop
    p*=2.5;
    p.x*=5.0;
    p.x -= 3.5; p.y -= 10.5;
    p.y += smoothstep(0.0, 0.99, fract(iTime*0.125)) * 20.0;
    p.x = (mod(p.x, 3.0) / 1.5) * pow(abs(p.x), 5.0);
    d = smin(d, length(p), 1.85);

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy;
    vec2 uv = fragCoord/res.xy;

	vec2 p = 2.0*uv-1.0;

    p.x *= res.x/res.y;
    p *= 1.5;

	// noise
    p += noise(p*4.63)*0.075;
    p += noise(0.2*iTime+p*124.0)*0.015;

    // circle
    float c1 = step(circle(p), 0.95);
    vec3 col = vec3(c1*0.675, 0.8, 0.9);

    // organic grain
    float grain = texture(iChannel0, uv+noise(66.0*uv)*0.15).r; 
    grain += 1.5*step(grain*1.75, 0.575)*c1;
    col = mix(col, col*grain, 0.25*noise(p*120.5));

    // bg
    col = mix(1.25*vec3(0.175, 0.2, 0.2), col, c1);

    // Output to screen
    fragColor = vec4(col,1.0);
}
