vec3 drawline(float x, float stx) {
    return vec3(step(x,stx) * step(0.0, x+0.02 - stx));
}

vec3 drawrect(float vert1, float vert2, float horiz1, float horiz2, float sty, float stx) {
    return drawline(vert1, sty) + drawline(vert2, sty) + drawline(horiz1, stx) + drawline(horiz2, stx);
}

vec3 drawfilledgerect(float x, float stx, float y, float sty, vec3 color) {
    return vec3(step(x, stx)) * vec3(step(y, 1.0 - sty)) * color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     vec2 st = gl_FragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.875,0.834,0.808);

    color -= drawrect(0.1, abs(clamp(0.5, 0.9, sin(iTime*2.0))), 0.1, sin(iTime*2.0), st.x, st.y);
	color += drawline(0.4, st.x);
    color -= drawline(0.5, sin(st.x*iTime));
	color += drawline(0.14, sin(st.y*iTime));
    color -= drawline(0.8, sin(st.y*iTime));
	color -= drawfilledgerect(0.8, st.x, 0.1, st.y, vec3(abs(cos(iTime)),1.000,abs(sin(iTime))));
    color -= drawfilledgerect(0.9, 1.0 - st.x, sin(iTime), st.y, vec3(abs(sin(iTime)), 1.0, 0.0));
    color -= drawfilledgerect(0.5, 1.0 - st.x, 0.8, 1.0 - st.y, vec3(0.140,0.219,1.000));

    fragColor = vec4(color,1.0);
}
