void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.yy;

    vec2 fv1 = uv - vec2(0.55, 0.5);
    float r1 = length(fv1);
    float f1 = 0.025/(0.025 + r1*r1);
    fv1 = normalize(fv1) * f1;

    vec2 fv2 = uv - vec2(1.15, 0.5);
    float r2 = length(fv2);
    float f2 = 0.025/(0.025 +  r2*r2);
    fv2 = normalize(fv2) * f2;

    vec3 col = mix(vec3(0.,0.,0.),vec3(1.,1.,1.), cos(10.*(iTime*0.25) + 100. * length(fv1 - fv2)));


    // Output to screen
    fragColor = vec4(col,1.0);
}
