float f_uv(float x, float time){
float pi = 3.141592653589793238462643383279;  
   if (sin(.5 * pi * (time + 1.)) < 0.) {
      time = time * .5 * pi;
   }
   else {
      time = time * .5 * pi + pi;
   }
   x = x + asin(sin(time)) / pi;
   return x;
}
