float f(float n, vec3 hsl)
{
    float k = mod((n + hsl.x / 30.0), 12.0);
    float a = hsl.y * min(hsl.z, 1.0 - hsl.z);

    return hsl.z - a * max(-1.0, min(min(k - 3.0, 9.0 - k), 1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy - vec2(0.5);

    uv.y += sin(12.0 * uv.x + iTime) / 8.0;
    uv.x += cos(8.0 * uv.y + iTime / 2.0) / 4.0;

    uv.y += sin(20.0 * uv.x + iTime * 4.0) / 16.0;
    uv.x += cos(48.0 * uv.y + iTime * 2.0) / 32.0;

    uv.y += cos(15.0 * uv.x + iTime / 2.0) / 20.0;
    uv.x += sin(32.0 * uv.y + iTime * 2.0) / 40.0;

    float dx, dy, dis;
    vec2 coord = fragCoord / vec2(max(iResolution.x, iResolution.y));
    vec2 mouse = iMouse.xy / vec2(max(iResolution.x, iResolution.y));

    dx = coord.x - mouse.x;
    dy = coord.y - mouse.y;
    dis = sqrt(dx*dx + dy*dy);

    vec3 hsl = vec3(0.0, 1.0, 0.5);


    if (dis < 0.15)
    {
        if (dis < 0.1)
        {
            uv.x *= sqrt(abs(dx));
            uv.y *= sqrt(abs(dy));
            hsl.z = 1.0 - dis * 10.0;
        }
        else
        {
            float progress = 1.0 - (dis - 0.1) * 20.0;
            progress *= progress;

            uv.x = uv.x * (1.0 - progress) + (fragCoord.x / iResolution.x) * progress;
            uv.y = uv.y * (1.0 - progress) + (fragCoord.y / iResolution.y) * progress;
        }
    }

    hsl.x = sqrt(uv.x * uv.x + uv.y * uv.y) * 7200.0 - iTime * 270.0 + (uv.x > 0.0 ? 180.0 : 0.0) + atan(uv.y / uv.x) * 180.0 / 3.14159265358;
    vec3 rgb = vec3(0.0);

    //hsl.x = round(hsl.x / 45.0) * 45.0;

    rgb.x = f(0.0, hsl);
    rgb.y = f(8.0, hsl);
    rgb.z = f(4.0, hsl);

    fragColor = vec4(rgb, 1.0);

    vec3 col = vec3(mod(hsl.x, 360.0) >= 180.0 ? 1.0 : 0.0);

    //fragColor = vec4(col, 1.0);


    //if (mod(uv.x, 0.1) > 0.095 || mod(uv.y, 0.1) > 0.095) fragColor = vec4(1.0);
    //else fragColor = vec4(0.0);

}
