vec3 light = vec3(1.4,1.1,0.7);
vec3 light2 = vec3(1.4,0,0.7);
vec3 light3 = vec3(0,0.5,0.7);
float radius = 2.1;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // iResolution vec2(800,450)
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv =  (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;

    //This is the supposed to be Input 
    float timer= 0.3 + 0.8*abs(sin(iTime));

    float factor1 = pow(length(uv) / timer * radius, 9.0*timer);



    if(uv.x>0.)
    {
        vec3 col = vec3(0.0);
        if(factor1 <= 1.0)
        {
	    col = mix(light3,light3, factor1);
        }
        else
        {
	    col = mix(light2, col, 0.54*(factor1 - 1.0));
        }
        // Output to screen
        fragColor = vec4(col,0.5);
    }
    else
    {
        vec3 col = vec3(0.0);
        if(factor1 <= 1.0)
        {
	    col = mix(light,light, factor1);
        }
        else
        {
	    col = mix(light2, col, 0.54*(factor1 - 1.0));
        }
        // Output to screen
        fragColor = vec4(col,0.5);
    }

}
