#define R(a) mat2(cos(a),sin(a),-sin(a),cos(a))
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    for(float i,g,e;
        ++i<70.;
        e<5e-4?O+=.8/i:O
        )
    {
        vec3 r=iResolution,k=vec3(5,2,1),
        p=g*vec3((C-.5*r.xy)/r.y,1);
        p.xz*=R(iTime*.2);
        p.y+=5.5;
        for(int j=0;++j<8;)
        {
            p.xz=abs(p.xz);
            p.xz=p.z>p.x?p.zx:p.xz;
            p.z=.9-abs(p.z-.9);
            p.xy=p.y>p.x?p.yx:p.xy;
            p.x-=2.3;
            p.xy=p.y>p.x?p.yx:p.xy;
            p.y+=.1;
            p=k+(p-k)*3.2;
        }
        g+=e=length(p)/6e3-.001;
    }
}
