renderpass.code:
float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(98.765, 43.21))) * 31415.);
}

float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // 平滑插值
    vec2 u = f * f * (10. - 50. * f);

    // 四个随机点的插值
    return mix(
        mix(random(i + vec2(0., 0.)), random(i + vec2(1., 0.)), u.x),
        mix(random(i + vec2(0., 1.)), random(i + vec2(1., 1.)), u.x),
        u.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    float t = iTime * 0.8;

    float g = noise(uv * 3. + t + 1.);   
    float b = noise(uv * 7. + t + 70.); 

    // Σ
    fragColor = vec4(g*.01, g, b, 0.1);
}
