void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    uv.x *= (iResolution.x / iResolution.y);

    uv += iTime * 0.2;
    uv = fract(uv * 2.0) -0.5;

    mat2 rotation = mat2(cos(iTime), sin(iTime), -sin(iTime), cos(iTime));
    mat2 r2 = mat2(cos(iTime * 0.5), sin(iTime * 0.5), -sin(iTime * 0.5), cos(iTime * 0.5));

    vec2 uvd = uv * r2;
    uv *= rotation;

    float f = step(max(abs(uv.x), abs(uv.y)) + step(max(abs(uvd.x), abs(uvd.y)) - 0.4, 0.0) - 0.6, 0.0);

    fragColor = vec4(vec3(f), 1.0);

}
