void mainImage( out vec4 o, vec2 u )
{
    vec2 uv = u / iResolution.x;
    vec3 f;

    for (float i = 0.; i++ < 20.;)
    {
        float t = mix(-.25, .75, fract(n(i) + iTime * .1));
        vec2 uv2 = uv - vec2(mix(.5, .5 + sin(t*10. + i)*.025*i, sin(6.28*(t))), t);
        float f2 = mix(length(uv2) - .05, s(uv2, .05, .025), min( 1.,1.7*sin(1e2*t)-1. ));
        f = mix(f, p(fract(6.*(t+f2)+i/20.)), floor(1.-f2));
    }

    o = vec4(f,1.);
}

//https://iquilezles.org/articles/distfunctions2d/
float s(vec2 p, float r, float t)
{
    vec2 u = vec2(0.809016994375, -0.587785252292), v = vec2(-u.x,u.y);
    p.x = abs(p.x);
    p -= 2.0*max(dot(u,p),0.0)*u;
    p -= 2.0*max(dot(v,p),0.0)*v;
    p.x = abs(p.x);
    p.y -= r;
    vec2 ba = t*vec2(-u.y,u.x) - vec2(0,1);
    float h = clamp( dot(p,ba)/dot(ba,ba), 0.0, r );
    return length(p-ba*h) * sign(p.y*ba.x-p.x*ba.y);
}

vec3 p(float t)
{
    return vec3(.5) + vec3(.5)*cos( 6.28318*(vec3(1.)*t+vec3(0.,.33,.67)) );
}

float n(float a) {
    return fract(fract(a*2384.293)*512.3453+a*128.7393);
}
