//basically this shader depends on your internet speed, the 4 videos will be loaded a slighlty
//different offsets when you load the shader..... yeah i know...do not say nothing. 
//if you have a fast internet it does not work well probably, 
//just randomly pause the 4 videos below and play again so you offset them 




void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;

    // Sample the input buffers
    vec4 tex0 = texture(iChannel0, uv);
    vec4 tex1 = texture(iChannel1, uv);
    vec4 tex2 = texture(iChannel2, uv);
    vec4 tex3 = texture(iChannel3, uv);

    // Calculate the strobe effect based on the current frame
    float strobe = mod(float(iFrame), 4.0);

    // Mix the four textures based on the strobe effect
    vec4 mixedColor = tex0;
    if (strobe < 1.0) {
        mixedColor = tex0;
    } else if (strobe < 2.0) {
        mixedColor = tex1;
    } else if (strobe < 3.0) {
        mixedColor = tex2;
    } else {
        mixedColor = tex3;
    }

    // Output the mixed color
    fragColor = mixedColor;
}
